using System;using System.Collections.ObjectModel;using Avalonia.Controls;using Avalonia.schoolTimetabler.Views;using ReactiveUI;using Avalonia.Data;namespace Avalonia.schoolTimetabler.ViewModels{    public class CreateSchoolProfileViewModel : ViewModelBase, IRoutableViewModel    {        private string? _userName;        private string? _password;        private string? _schoolNumber;        private string? _fullName;        public CreateSchoolProfileViewModel(MainWindowViewModel mainWindowViewModel)        {                    }        public CreateSchoolProfileViewModel()        {        }        public string FullName        {            set            {                if (value != null && !string.Equals(value, ""))                {                    this.RaiseAndSetIfChanged(ref _fullName, value);                }            }            get            {                if (_fullName != null)                    return _fullName;                                throw new NullReferenceException();            }        }                public string SchoolNumber        {            set            {                if (value != null && !string.Equals(value, ""))                {                    this.RaiseAndSetIfChanged(ref _schoolNumber, value);                }            }            get            {                if (_schoolNumber != null)                    return _schoolNumber;                                throw new NullReferenceException();            }        }                public string Password        {            set            {                if (value != null && !string.Equals(value, ""))                {                    this.RaiseAndSetIfChanged(ref _password, value);                }            }            get            {                if (_password != null)                    return _password;                                throw new NullReferenceException();            }        }                public string UserName        {            set            {                if (value != null && !string.Equals(value, ""))                {                    this.RaiseAndSetIfChanged(ref _userName, value);                }            }            get            {                if (_userName != null)                    return _userName;                                throw new NullReferenceException();            }        }        public string? UrlPathSegment { get; }        public IScreen HostScreen { get; }    }}