using System;using System.Reactive;using System.Windows.Input;using Avalonia.Controls;using Avalonia.Input;using ReactiveUI;namespace Avalonia.schoolTimetabler.ViewModels{    public class MainWindowViewModel : ViewModelBase, IScreen    {        public RoutingState Router { get; } = new RoutingState();        public ReactiveCommand<Unit, IRoutableViewModel> GoNext { get; }        public ReactiveCommand<Unit, Unit> GoBack => Router.NavigateBack;                public MainWindowViewModel()        {            GoNext = ReactiveCommand.CreateFromObservable(                () => Router.Navigate.Execute(new CreateSchoolProfileViewModel(this))            );        }    }}