using System.Reactive;using ReactiveUI;namespace Avalonia.schoolTimetabler.ViewModels{    public class MainWindowViewModel : ViewModelBase, IScreen    {        public RoutingState Router { get; } = new RoutingState();        public ReactiveCommand<Unit, IRoutableViewModel> GoCreateProfile { get; }        public MainWindowViewModel()        {            GoCreateProfile = ReactiveCommand.CreateFromObservable(                () => Router.Navigate.Execute(new CreateSchoolProfileViewModel(this))            );        }    }}