using Data.Models;namespace Data.FakeDataBase;public class FDataBaseCabinets{    private readonly List<SchoolCabinet> _schoolCabinets;    public IEnumerable<SchoolCabinet> SchoolCabinets { get; set; }    private FDataBaseCabinets()    {        _schoolCabinets = new List<SchoolCabinet>();        SchoolCabinets = _schoolCabinets;    }        public void AddCabinet(SchoolCabinet schoolCabinet)    {        _schoolCabinets.Add(schoolCabinet);        SchoolCabinets = _schoolCabinets;    }    public void DeleteCabinet(int index)    {        _schoolCabinets.Remove(_schoolCabinets[index]);        SchoolCabinets = _schoolCabinets;    }    public static FDataBaseCabinets GetInstance()    {        return _instance ??= new FDataBaseCabinets();    }    private static FDataBaseCabinets? _instance = null;}