using Data.Models;namespace Data.FakeDataBase;public class FDataBaseClasses{    private readonly List<SchoolClass> _schoolClasses;    public IEnumerable<SchoolClass> SchoolClasses { get; set; }    private FDataBaseClasses()    {        _schoolClasses = new List<SchoolClass>();        SchoolClasses = _schoolClasses;    }        public void AddClass(SchoolClass schoolClass)    {        _schoolClasses.Add(schoolClass);        SchoolClasses = _schoolClasses;    }    public static FDataBaseClasses GetInstance()    {        return _instance ??= new FDataBaseClasses();    }    private static FDataBaseClasses? _instance = null;}