using Data.Models;namespace Data.FakeDataBase;public class FDataBase{    private readonly List<SchoolClass> _schoolClasses;    public IEnumerable<SchoolClass> SchoolClasses { get; }    private FDataBase()    {        _schoolClasses = new List<SchoolClass>();        SchoolClasses = _schoolClasses;    }        public void AddClass(SchoolClass schoolClass)    {        _schoolClasses.Add(schoolClass);    }    public static FDataBase GetInstance()    {        if (_instance == null) _instance = new FDataBase();                return _instance;    }    private static FDataBase? _instance = null;}