using Data.Models;namespace Data.FakeDataBase;public class FDataBaseClasses{    private readonly List<SchoolClass> _schoolClasses;    public IEnumerable<SchoolClass> SchoolClasses { get; }        private FDataBaseClasses()    {        _schoolClasses = new List<SchoolClass>();        SchoolClasses = _schoolClasses;    }        public void AddClass(SchoolClass schoolClass)    {        _schoolClasses.Add(schoolClass);    }    public static FDataBaseClasses GetInstance()    {        return _instance ?? (_instance = new FDataBaseClasses());    }    private static FDataBaseClasses? _instance = null;}