using Data.Models;namespace Data.FakeDataBase;public class FDataBaseClasses{    private static FDataBaseClasses? _instance;    private readonly List<SchoolClass> _schoolClasses;    private FDataBaseClasses()    {        _schoolClasses = new List<SchoolClass>();        SchoolClasses = _schoolClasses;    }    public IEnumerable<SchoolClass> SchoolClasses { get; set; }    public void AddClass(SchoolClass schoolClass)    {        _schoolClasses.Add(schoolClass);        SchoolClasses = _schoolClasses;    }    public void DeleteClass(int index)    {        _schoolClasses.Remove(_schoolClasses[index]);        SchoolClasses = _schoolClasses;    }    public static FDataBaseClasses GetInstance()    {        return _instance ??= new FDataBaseClasses();    }}