using Data.Models;namespace Data.FakeDataBase;public class FDataBaseDisciplines{    private static FDataBaseDisciplines? _instance;    private readonly List<SchoolDiscipline> _schoolDisciplines;    private FDataBaseDisciplines()    {        _schoolDisciplines = new List<SchoolDiscipline>();        SchoolDisciplines = _schoolDisciplines;    }    public IEnumerable<SchoolDiscipline> SchoolDisciplines { get; set; }    public void AddClass(SchoolDiscipline schoolDiscipline)    {        _schoolDisciplines.Add(schoolDiscipline);        SchoolDisciplines = _schoolDisciplines;    }    public void DeleteDiscipline(int index)    {        _schoolDisciplines.Remove(_schoolDisciplines[index]);        SchoolDisciplines = _schoolDisciplines;    }    public static FDataBaseDisciplines GetInstance()    {        return _instance ??= new FDataBaseDisciplines();    }}