using Data.Models;namespace Data.FakeDataBase;public class FakeDataBaseSchoolInfo{    private School _schoolInfo;    public School SchoolInfo { get; set; }    private FakeDataBaseSchoolInfo()    {        _schoolInfo = new School("Не задано", "Не задано", "Не задано", "Не задано");        SchoolInfo = _schoolInfo;    }    public void AddInfo(School School)    {        _schoolInfo = School;        SchoolInfo = _schoolInfo;    }    public static FakeDataBaseSchoolInfo GetInstance()    {        return _instance ??= new FakeDataBaseSchoolInfo();    }    private static FakeDataBaseSchoolInfo? _instance = null;}