using Data.Models;namespace Data.FakeDataBase;public class FDataBaseSchoolLessons{    private readonly List<SchoolLesson> _schoolLessons;    public IEnumerable<SchoolLesson> SchoolLessons { get; }        private FDataBaseSchoolLessons()    {        _schoolLessons = new List<SchoolLesson>();        SchoolLessons = _schoolLessons;    }        public void AddClass(SchoolLesson schoolLesson)    {        _schoolLessons.Add(schoolLesson);    }    public static FDataBaseSchoolLessons GetInstance()    {        return _instance ?? (_instance = new FDataBaseSchoolLessons());    }    private static FDataBaseSchoolLessons? _instance = null;}