using Data.Models;namespace Data.FakeDataBase;public class FDataBaseTeachers{    private readonly List<SchoolTeachers> _schoolTeachers;    public IEnumerable<SchoolTeachers> SchoolTeachers { get; set; }    private FDataBaseTeachers()    {        _schoolTeachers = new List<SchoolTeachers>();        SchoolTeachers = _schoolTeachers;    }        public void AddTeacher(SchoolTeachers schoolTeachers)    {        _schoolTeachers.Add(schoolTeachers);        SchoolTeachers = _schoolTeachers;    }        public void DeleteDiscipline(int index)    {        _schoolTeachers.Remove(_schoolTeachers[index]);        SchoolTeachers = _schoolTeachers;    }    public void AddDiscipline(string str, int index)    {        _schoolTeachers[index].TeacherDisciplines = str;        SchoolTeachers = _schoolTeachers;    }    public static FDataBaseTeachers GetInstance()    {        return _instance ??= new FDataBaseTeachers();    }    private static FDataBaseTeachers? _instance = null;}