using Domain.Entities;using Domain.Repositories;namespace Data.Repositories;public class CabinetsRepository : SerializationRepository<Cabinet>, ICabinetsRepository<Cabinet>{    private static CabinetsRepository? _globalRepositoryInstance;    protected CabinetsRepository(string path) : base(path)    {    }    public void Delete(Cabinet delCabinet)    {        Remove(delCabinet);    }    public void Add(Cabinet newCabinet)    {        Append(newCabinet);    }    public List<Cabinet> Read()    {        return DeserializationJson();    }    public static CabinetsRepository GetInstance()    {        return _globalRepositoryInstance ??=            new CabinetsRepository("../../../../Data/DataSets/Cabinets.json");    }    public override bool CompareEntities(Cabinet entity1, Cabinet entity2)    {        return entity1.CabinetNumber == entity2.CabinetNumber;    }}