using Domain.Entities;using Domain.Repositories;namespace Data.Repositories;public class ClassesRepository : SerializationRepository<Class>, IClassesRepository<Class>{    private static ClassesRepository? _globalRepositoryInstance;    protected ClassesRepository(string path) : base(path)    {    }    public void Delete(Class delClass)    {        Remove(delClass);    }    public void Add(Class newClass)    {        Append(newClass);    }    public List<Class> Read()    {        return DeserializationJson();    }    public static ClassesRepository GetInstance()    {        return _globalRepositoryInstance ??=            new ClassesRepository("../../../../Data/DataSets/Classes.json");    }    public override bool CompareEntities(Class entity1, Class entity2)    {        return entity1.Number == entity2.Number && entity1.Symbol == entity2.Symbol;    }}