using Domain.Entities;using Domain.Repositories;namespace Data.Repositories;public class DisciplineRepository : SerializationRepository<Discipline>, IDisciplinesRepository<Discipline>{    private static DisciplineRepository? _globalRepositoryInstance;    protected DisciplineRepository(string path) : base(path)    {    }    public void Delete(Discipline delDiscipline)    {        Remove(delDiscipline);    }    public void Add(Discipline newDiscipline)    {        Append(newDiscipline);    }    public List<Discipline> Read()    {        return DeserializationJson();    }    public static DisciplineRepository GetInstance()    {        return _globalRepositoryInstance ??= new DisciplineRepository("../../../../Data/DataSets/Disciplines.json");    }    public override bool CompareEntities(Discipline entity1, Discipline entity2)    {        return entity1.DisciplineName == entity2.DisciplineName;    }}