using System.Reactive.Subjects;namespace Data.Repositories;public class SerializationRepository<T>{    private Stream? _fs;    private readonly string _path;    protected IObservable<List<T>> AsObservable { get; }    private BehaviorSubject <List<T>> EntitiesSubject { get; }    protected SerializationRepository(string path)    {        _path = path;        EntitiesSubject = new BehaviorSubject<List<T>>(new List<T>());            }}