using Domain.Entities;using Domain.Repositories;namespace Data.Repositories;public class UserRepository : SerializationRepository<User>, IUserRepository<User>{    private static UserRepository? _globalRepositoryInstance;    protected UserRepository(string path) : base(path)    {    }    public static UserRepository GetInstance()    {        return _globalRepositoryInstance ??= new UserRepository("../../../../Data/DataSets/User.json");    }    public void Delete()    {            }        public void Add(User newEntity)    {        Append(newEntity);        if (DeserializationJson().Count == 2) Remove(DeserializationJson()[0]);    }    public User Read()    {        if (DeserializationJson().Count == 1) return DeserializationJson()[0];        return new User("Не задано", "Не задано");    }    public override bool CompareEntities(User entity1, User entity2)    {        return entity1.Post.Equals(entity2.Post)               && entity1.FullName.Equals(entity2.FullName);    }}