namespace Domain.Entities;[Serializable]public class Teacher{    public Teacher(string teacherFullName, int id, List<string> teacherDisciplines)    {        TeacherFullName = teacherFullName;        Id = id;        TeacherDisciplines = teacherDisciplines;    }    public List<string> TeacherDisciplines { get; set; }    public int Id { get; }    public string TeacherFullName { get; set; }}