using Domain.Entities;using Domain.Repositories;using MessageBox.Avalonia;namespace Domain.UseCases;public class CabinetInteractor{    private readonly ICabinetsRepository<Cabinet> _cabinetsRepository;    public CabinetInteractor(ICabinetsRepository<Cabinet> cabinetsRepository)    {        _cabinetsRepository = cabinetsRepository;    }    public void AddCabinet(string cabinetName)    {        var detected = false;        foreach (var Cabinet in _cabinetsRepository.Read())            if (Cabinet.CabinetNumber == cabinetName)            {                var message = MessageBoxManager                    .GetMessageBoxStandardWindow("Неправильные данные",                        "Вы неправильно заполинили поле: Такой кабинет уже существует").Show();                detected = true;                return;            }        if (string.IsNullOrWhiteSpace(cabinetName) && detected == false)        {            var message = MessageBoxManager                .GetMessageBoxStandardWindow("Неправильные данные",                    "Вы не заполинили поле: Номер кабинета").Show();        }        else        {            var newCabinet = new Cabinet(cabinetName);            _cabinetsRepository.Add(newCabinet);        }    }    public void DelCabinet(Cabinet cabinet)    {        _cabinetsRepository.Delete(cabinet);    }    public List<Cabinet> GetCabinets()    {        var t = _cabinetsRepository.Read();        return t;    }}