using Domain.Entities;using Domain.Repositories;namespace Domain.UseCases;public class ClassInteractor{    private readonly IClassesRepository<Class> _classesRepository;    public ClassInteractor(IClassesRepository<Class> classesRepository)    {        _classesRepository = classesRepository;    }        public void AddClass(Class newClass)    {        _classesRepository.Add(newClass);    }    public void DelClass(Class delClass)    {        _classesRepository.Delete(delClass);    }    public List<Class> GetClasses()    {        var t = _classesRepository.Read();        return t;    }}