using Domain.Entities;using Domain.Repositories;namespace Domain.UseCases;public class DisciplineInteractor{    private readonly IDisciplinesRepository<Discipline> _disciplinesRepository;    public DisciplineInteractor(IDisciplinesRepository<Discipline> disciplinesRepository)    {        _disciplinesRepository = disciplinesRepository;    }        public void AddDiscipline(Discipline newDiscipline)    {        _disciplinesRepository.Add(newDiscipline);    }    public void DelDiscipline(Discipline delDiscipline)    {        _disciplinesRepository.Delete(delDiscipline);    }    public List<Discipline> GetDisciplines()    {        var t = _disciplinesRepository.Read();        return t;    }}