using System.Security.Cryptography;using System.Text;using Domain.Entities;using Domain.Repositories;namespace Domain.UseCases;public class PasswordInteractor{    private readonly IUserRepository<User> _userRepository;    private static string _securityKey = "ComplexKeyHere_12121";    private static string _encPassword;    public PasswordInteractor(IUserRepository<User> userRepository)    {        _userRepository = userRepository;    }    public bool CheckEqualPassword(string inputPassword)    {        var CipherText = _userRepository.Read().Password;        var toEncryptArray = Convert.FromBase64String(CipherText);        var objMD5CryptoService = new MD5CryptoServiceProvider();        var securityKeyArray = objMD5CryptoService.ComputeHash(Encoding.UTF8.GetBytes(_securityKey));        objMD5CryptoService.Clear();        var objTripleDESCryptoService = new TripleDESCryptoServiceProvider();        objTripleDESCryptoService.Key = securityKeyArray;        objTripleDESCryptoService.Mode = CipherMode.ECB;        objTripleDESCryptoService.Padding = PaddingMode.PKCS7;        var objCrytpoTransform = objTripleDESCryptoService.CreateDecryptor();        var resultArray = objCrytpoTransform.TransformFinalBlock(toEncryptArray, 0, toEncryptArray.Length);        objTripleDESCryptoService.Clear();        return Encoding.UTF8.GetString(resultArray) == inputPassword;    }    public void EncryptPlainTextToCipherText(string PlainText)    {        var toEncryptedArray = Encoding.UTF8.GetBytes(PlainText);        var objMD5CryptoService = new MD5CryptoServiceProvider();        var securityKeyArray = objMD5CryptoService.ComputeHash(Encoding.UTF8.GetBytes(_securityKey));        objMD5CryptoService.Clear();        var objTripleDESCryptoService = new TripleDESCryptoServiceProvider();        objTripleDESCryptoService.Key = securityKeyArray;        objTripleDESCryptoService.Mode = CipherMode.ECB;        objTripleDESCryptoService.Padding = PaddingMode.PKCS7;        var objCrytpoTransform = objTripleDESCryptoService.CreateEncryptor();        var resultArray = objCrytpoTransform.TransformFinalBlock(toEncryptedArray, 0, toEncryptedArray.Length);        objTripleDESCryptoService.Clear();        _encPassword = Convert.ToBase64String(resultArray, 0, resultArray.Length);        AddPasswordToUser();    }    private void AddPasswordToUser()    {        var t = _userRepository.Read();        t.Password = _encPassword;        t.KeyPassword = _securityKey;        _userRepository.Add(t);    }}