using Avalonia.Utilities;using Domain.Entities;using Domain.Repositories;namespace Domain.UseCases;public class SchoolInfoInteractor{    private readonly ISchoolInfoRepository<SchoolInfo> _schoolInfoRepository;        public SchoolInfoInteractor(ISchoolInfoRepository<SchoolInfo> schoolInfoRepository)    {        _schoolInfoRepository = schoolInfoRepository;    }    public void SchoolInfoSet(string fullNameDirector, string countClasses, string countTeachers, string schoolNumber)    {        var t = new List<string> { fullNameDirector, countClasses, countTeachers, schoolNumber };        List<String> msg = new List<string>();                for(int i = 0; i < 4; i++)        {            if (String.IsNullOrWhiteSpace(t[i]) && i == 0) msg.Add("ФИО директора");            if (String.IsNullOrWhiteSpace(t[i]) && i == 1) msg.Add("Количество классов");            if (String.IsNullOrWhiteSpace(t[i]) && i == 2) msg.Add("Количество учителей");            if (String.IsNullOrWhiteSpace(t[i]) && i == 3) msg.Add("Номер школы");        }                if (msg.Count != 0)        {            var message = MessageBox.Avalonia.MessageBoxManager                .GetMessageBoxStandardWindow("Неправильные данные",                    "Вы не заполинили одно или несколько полей в информации о школе:" + string.Join(", ", msg)).Show();        }        else        {            _schoolInfoRepository.Add(new SchoolInfo(schoolNumber, fullNameDirector, countClasses, countTeachers));        }    }    public SchoolInfo SchoolInfoGet()    {        var info = _schoolInfoRepository.Read();        return info;    }    public void SchoolInfoDel()    {        _schoolInfoRepository.Delete();    }}