using Domain.Entities;using Domain.Repositories;namespace Domain.UseCases;public class SchoolInfoInteractor{    private ISchoolInfoRepository<SchoolInfo> _schoolInfoRepository;    public SchoolInfoInteractor(ISchoolInfoRepository<SchoolInfo> schoolInfoRepository)    {        _schoolInfoRepository = schoolInfoRepository;    }    public void SchoolInfoSet(SchoolInfo schoolInfo)    {        _schoolInfoRepository.Add(schoolInfo);    }    public SchoolInfo SchoolInfoGet()    {        var info = _schoolInfoRepository.GetSchoolInfo();        return info;    }}