using Avalonia.Controls;using Domain.Entities;using Domain.Repositories;namespace Domain.UseCases;public class UserInteractor{    private readonly IUserRepository<User> _userRepository;    public UserInteractor(IUserRepository<User> userRepository)    {        _userRepository = userRepository;    }    public void UserSet(string post, string userName)    {        if (String.IsNullOrWhiteSpace(post) || String.IsNullOrWhiteSpace(userName))        {            if (String.IsNullOrWhiteSpace(post) && String.IsNullOrWhiteSpace(userName) == false)            {                var message = MessageBox.Avalonia.MessageBoxManager                    .GetMessageBoxStandardWindow("Неправильные данные",                        "Вы не заполинили одно или несколько полей в информации о пользователе: Должность").Show();            }            if (String.IsNullOrWhiteSpace(userName) && String.IsNullOrWhiteSpace(post) == false)            {                var message = MessageBox.Avalonia.MessageBoxManager                    .GetMessageBoxStandardWindow("Неправильные данные",                        "Вы не заполинили одно или несколько полей в информации о пользователе: ФИО редактора").Show();            }            if (String.IsNullOrWhiteSpace(userName) && String.IsNullOrWhiteSpace(post))            {                var message = MessageBox.Avalonia.MessageBoxManager                    .GetMessageBoxStandardWindow("Неправильные данные",                        "Вы не заполинили одно или несколько полей в информации о пользователе: ФИО редактора, Должность").Show();            }        }        else _userRepository.Add(new User(userName, post));    }    public User UserGet()    {        var info = _userRepository.Read();        return info;    }    public void SchoolInfoDel()    {        _userRepository.Delete();    }}