using ReactiveUI;using SchoolTimetabler.ViewModels;using SchoolTimetabler.Views;namespace SchoolTimetabler;public class AppViewLocator : IViewLocator{    public IViewFor ResolveView<T>(T viewModel, string contract = null)    {        return viewModel switch        {            ClassEditingMenuViewModel context => new ClassEditingMenu { DataContext = context },            DisciplineEditingMenuViewModel context => new DisciplineEditingMenu { DataContext = context },            CreateSchoolProfileViewModel context => new CreateSchoolProfile { DataContext = context },            TeacherEditingMenuViewModel context => new TeacherEditingMenu { DataContext = context },            SchoolInfoViewModel context => new SchoolInfo { DataContext = context },            CreateTimetableViewModel context => new CreateTimetable { DataContext = context },            CabinetEditingMenuViewModel context => new CabinetEditingMenu { DataContext = context },            TimetableInfoViewModel context => new TimetableInfo { DataContext = context },            UserInformationViewModel context => new UserInformation { DataContext = context },            SchoolInformationViewModel context => new SchoolInformation { DataContext = context },            HelpInformationViewModel context => new HelpInformation { DataContext = context },            LockWorkPlaceViewModel context => new WorkPlaceLock { DataContext = context }        };    }}