using Data.Repositories;using Domain.UseCases;using MessageBox.Avalonia;using ReactiveUI;using ReactiveUI.Fody.Helpers;namespace SchoolTimetabler.ViewModels;public class LockWorkPlaceViewModel : ViewModelBase, IRoutableViewModel, IScreen{    private readonly MainWindowViewModel _mainWindowViewModel;    private readonly PasswordInteractor passwordInteractor;    public LockWorkPlaceViewModel(MainWindowViewModel mainWindowViewModel)    {        passwordInteractor = new PasswordInteractor(UserRepository.GetInstance());        _mainWindowViewModel = mainWindowViewModel;    }    [Reactive] public string InputPassword { get; set; }    [Reactive] public bool IsVisible { get; set; } = true;    public string? UrlPathSegment { get; }    public IScreen HostScreen { get; }    public RoutingState Router { get; }    public void PasswordConfirm()    {        if (passwordInteractor.CheckEqualPassword(InputPassword))        {            _mainWindowViewModel.LockWorkPasswordTrue();            IsVisible = false;        }        else        {            var msg = MessageBoxManager                .GetMessageBoxStandardWindow("Пароль", "Вы ввели неверный пароль").Show();        }    }}