using Data.Repositories;using Domain.UseCases;using ReactiveUI;using ReactiveUI.Fody.Helpers;namespace SchoolTimetabler.ViewModels;public class LockWorkPlaceViewModel : ViewModelBase, IRoutableViewModel, IScreen{    [Reactive] public string InputPassword { get; set; }    [Reactive] public bool IsVisible { get; set; } = true;    private PasswordInteractor passwordInteractor;    private readonly MainWindowViewModel _mainWindowViewModel;        public LockWorkPlaceViewModel(MainWindowViewModel mainWindowViewModel)    {        passwordInteractor = new PasswordInteractor(UserRepository.GetInstance());        _mainWindowViewModel = mainWindowViewModel;    }        public void PasswordConfirm()    {        if (passwordInteractor.CheckEqualPassword(InputPassword) == true)        {            _mainWindowViewModel.LockWorkPasswordTrue();            IsVisible = false;        }        else        {            var msg = MessageBox.Avalonia.MessageBoxManager                .GetMessageBoxStandardWindow("Пароль", "Вы ввели неверный пароль");        }    }        public string? UrlPathSegment { get; }    public IScreen HostScreen { get; }    public RoutingState Router { get; }}