using System;using System.Collections.Generic;using System.Reactive;using Avalonia.Styling;using Data.FakeDataBase;using ReactiveUI;namespace SchoolTimetabler.ViewModels{    public class MainWindowViewModel : ViewModelBase, IScreen    {        public RoutingState Router { get; } = new RoutingState();        public ReactiveCommand<Unit, IRoutableViewModel> GoCreateProfile { get; }        public ReactiveCommand<Unit, IRoutableViewModel> GoCreateTimetable { get; }        public ReactiveCommand<Unit, IRoutableViewModel> GoSchoolInfo { get; }        public List<string> DisciplinesName = new List<string>();        public MainWindowViewModel()        {            _storageSchoolInfo = FakeDataBaseSchoolInfo.GetInstance();            _storageClasses = FDataBaseClasses.GetInstance();            _storageDisciplines = FDataBaseDisciplines.GetInstance();            _storageTeachers = FDataBaseTeachers.GetInstance();            _storageTimetables = FDataBaseTimetable.GetInstance();                        GoCreateTimetable = ReactiveCommand.CreateFromObservable(                () => Router.Navigate.Execute(new CreateTimetableViewModel(this))            );            GoSchoolInfo = ReactiveCommand.CreateFromObservable(                () => Router.Navigate.Execute(new SchoolInfoViewModel(this, _storageSchoolInfo, _storageClasses,                    _storageDisciplines, _storageTeachers))            );            GoCreateProfile = ReactiveCommand.CreateFromObservable(                () => Router.Navigate.Execute(new CreateSchoolProfileViewModel(this, _storageSchoolInfo,                    _storageClasses,                    _storageDisciplines, _storageTeachers, DisciplinesName))            );        }        private FDataBaseTimetable _storageTimetables;        private FakeDataBaseSchoolInfo _storageSchoolInfo;        private FDataBaseClasses _storageClasses;        private FDataBaseDisciplines _storageDisciplines;        private FDataBaseTeachers _storageTeachers;    }}