using System.Reactive;using Data.FakeDataBase;using ReactiveUI;namespace SchoolTimetabler.ViewModels{    public class MainWindowViewModel : ViewModelBase, IScreen    {        public RoutingState Router { get; } = new RoutingState();        public ReactiveCommand<Unit, IRoutableViewModel> GoCreateProfile { get; }        public ReactiveCommand<Unit, IRoutableViewModel> GoSchoolInfo { get; }        private FakeDataBaseSchoolInfo _storageSchoolInfo;        private FDataBaseClasses _storageClasses;        private FDataBaseDisciplines _storageDisciplines;        private FDataBaseTeachers _storageTeachers;        public MainWindowViewModel()        {            _storageSchoolInfo = FakeDataBaseSchoolInfo.GetInstance();            _storageClasses = FDataBaseClasses.GetInstance();            _storageDisciplines = FDataBaseDisciplines.GetInstance();            GoSchoolInfo = ReactiveCommand.CreateFromObservable(                () => Router.Navigate.Execute(new SchoolInfoViewModel(this, _storageSchoolInfo, _storageClasses, _storageDisciplines))            );            GoCreateProfile = ReactiveCommand.CreateFromObservable(                () => Router.Navigate.Execute(new CreateSchoolProfileViewModel(this, _storageSchoolInfo, _storageClasses, _storageDisciplines, _storageTeachers))            );        }    }}