using System;using System.Collections.Generic;using System.Collections.ObjectModel;using System.ComponentModel;using System.Reactive;using System.Reactive.Linq;using System.Reactive.Subjects;using System.Runtime.CompilerServices;using System.Threading.Tasks;using Avalonia.Collections;using Avalonia.Controls;using Avalonia.Input;using Avalonia.Interactivity;using Data.FakeDataBase;using Data.Models;using ReactiveUI;namespace SchoolTimetabler.ViewModels;public class SchoolInfoViewModel : ViewModelBase, IRoutableViewModel, IScreen{    private string? _schoolNumber;    private string? _fullNameDirector;    private string? _countClasses;    private string? _countTeacher;    private int _selectedIndexComboBox;    private int _selectedIndexComboBoxTeachers;    private bool _isVisiBleClsses = false;    private bool _isVisiBleDiscipline = false;    private bool _isVisiBleTeachers = false;    private bool _isVisibleDataGridTeachers = false;    private FakeDataBaseSchoolInfo _storage;    private FDataBaseClasses _storageClasses;    private FDataBaseDisciplines _storageDisciplines;    private FDataBaseTeachers _storageTeachers;    public ReactiveCommand<Unit, Unit> Output { get; }    public DataGridTextColumn Column;    private AvaloniaList<string> _disciplinesName;    public ObservableCollection<string> TeachersName { get; set; }    public ObservableCollection<Data.Models.SchoolClass> Classes { get; }    public ObservableCollection<Data.Models.SchoolDiscipline> Disciplines { get; }    public ObservableCollection<Data.Models.SchoolTeachers> Teachers { get; }    public SchoolInfoViewModel(MainWindowViewModel mainWindowViewModel,        FakeDataBaseSchoolInfo storage, FDataBaseClasses storageClasses, FDataBaseDisciplines storageDisciplines,        FDataBaseTeachers storageTeachers)    {        _storage = storage;        _storageClasses = storageClasses;        _storageDisciplines = storageDisciplines;        _storageTeachers = storageTeachers;        TeachersName = new ObservableCollection<string>();        Teachers = new ObservableCollection<Data.Models.SchoolTeachers>(_storageTeachers.SchoolTeachers);        Classes = new ObservableCollection<Data.Models.SchoolClass>(_storageClasses.SchoolClasses);        Disciplines = new ObservableCollection<Data.Models.SchoolDiscipline>(_storageDisciplines.SchoolDisciplines);        _disciplinesName = new AvaloniaList<string>();        _schoolNumber = _storage.SchoolInfo.SchoolNumber;        _fullNameDirector = _storage.SchoolInfo.FullNameDirector;        _countClasses = _storage.SchoolInfo.CountClasses;        _countTeacher = _storage.SchoolInfo.CountTeachers;    }    private void OutputTeacher()    {        DisciplinesName.Clear();        var buf = new ObservableCollection<string>();        buf = _storageTeachers.GetTeacherDiscipline(_selectedIndexComboBoxTeachers);        foreach (var t in buf)        {            DisciplinesName.Add(t);        }        IsVisibleDataGrigTeachers = true;    }    public void OutputTeachers()    {        TeachersName.Clear();        foreach (var t in Teachers)        {            TeachersName.Add(t.TeacherFullName);        }        IsVisibleTeachers = true;        IsVisibleDiscipline = false;        IsVisibleClasses = false;        OutputTeacher();    }    public void OutputClasses()    {        IsVisibleDiscipline = false;        IsVisibleDataGrigTeachers = false;        IsVisibleTeachers = false;        IsVisibleClasses = true;    }    public void OutputDisciplines()    {        IsVisibleClasses = false;        IsVisibleDataGrigTeachers = false;        IsVisibleTeachers = false;        IsVisibleDiscipline = true;    }    public AvaloniaList<string> DisciplinesName    {        get => _disciplinesName;        set => this.RaiseAndSetIfChanged(ref _disciplinesName, value);    }    public bool IsVisibleClasses    {        set => this.RaiseAndSetIfChanged(ref _isVisiBleClsses, value);        get => _isVisiBleClsses;    }    public bool IsVisibleDataGrigTeachers    {        set => this.RaiseAndSetIfChanged(ref _isVisibleDataGridTeachers, value);        get => _isVisibleDataGridTeachers;    }    public bool IsVisibleDiscipline    {        set => this.RaiseAndSetIfChanged(ref _isVisiBleDiscipline, value);        get => _isVisiBleDiscipline;    }    public int SelectedIndexComboBoxTeachers    {        set        {            this.RaiseAndSetIfChanged(ref _selectedIndexComboBoxTeachers, value);            IsVisibleDataGrigTeachers = false;            OutputTeacher();        }        get => _selectedIndexComboBoxTeachers;    }    public bool IsVisibleTeachers    {        set => this.RaiseAndSetIfChanged(ref _isVisiBleTeachers, value);        get => _isVisiBleTeachers;    }    public string? SchoolNumber    {        get => _schoolNumber;        set => this.RaiseAndSetIfChanged(ref _schoolNumber, value);    }    public string? FullNameDirector    {        get => _fullNameDirector;        set => this.RaiseAndSetIfChanged(ref _fullNameDirector, value);    }    public string? CountClasses    {        get => _countClasses;        set => this.RaiseAndSetIfChanged(ref _countClasses, value);    }    public string? CountTeacher    {        get => _countTeacher;        set => this.RaiseAndSetIfChanged(ref _countTeacher, value);    }    public string? UrlPathSegment { get; }    public IScreen HostScreen { get; }    public RoutingState Router { get; }}