using System.Reactive;using Data.Repositories;using Domain.UseCases;using ReactiveUI;using ReactiveUI.Fody.Helpers;namespace SchoolTimetabler.ViewModels;public class SchoolInformationViewModel : ViewModelBase, IRoutableViewModel, IScreen{    [Reactive] public string CountClasses { get; set; }    [Reactive] public string CountTeachers { get; set; }    [Reactive] public string FullNameDirector { get; set; }    [Reactive] public string SchoolNumber { get; set; }    public ReactiveCommand<Unit, Unit> ConfirmSchoolSettings { get; }    public SchoolInformationViewModel()    {        var schoolInfoInteractor = new SchoolInfoInteractor(SchoolRepository.GetInstance());        ConfirmSchoolSettings = ReactiveCommand.Create(() =>        {            schoolInfoInteractor.SchoolInfoSet(FullNameDirector, CountClasses, CountTeachers, SchoolNumber);        });    }    public string? UrlPathSegment { get; }    public IScreen HostScreen { get; }    public RoutingState Router { get; }}