using System.Reactive;using System.Reactive.Linq;using ReactiveUI;using ReactiveUI.Fody.Helpers;namespace SchoolTimetabler.ViewModels;public class SetPasswordViewModel : ViewModelBase, IRoutableViewModel, IScreen {    [Reactive]    public string Password { get; set; }        public SetPasswordViewModel()    {        Close = new Interaction<string, Unit>();                Confirm = ReactiveCommand.CreateFromTask(async () =>        {            await Close.Handle(Password);        });    }            public Interaction<string, Unit> Close { get; }    public ReactiveCommand<Unit, Unit> Confirm { get; }        public string? UrlPathSegment { get; }    public IScreen HostScreen { get; }    public RoutingState Router { get; }}