using System.Collections.ObjectModel;using System.Reactive;using Data.FakeDataBase;using Data.Models;using ReactiveUI;namespace SchoolTimetabler.ViewModels;public class TeacherEditingMenuViewModel : ViewModelBase, IRoutableViewModel, IScreen{    private bool _isEnabledContinue = false;    public ObservableCollection<Data.Models.SchoolTeachers> Teachers { get; set; }    public ObservableCollection<Data.Models.SchoolDiscipline> Disciplines { get; }    public ReactiveCommand<Unit, Unit> AddNewTeacher { get; }    public ReactiveCommand<Unit, Unit> DeleteTeacher { get; }    private int _dataGridSelectedIndex;    private FDataBaseTeachers _storage;    private FDataBaseDisciplines _storageDisciplines;    public TeacherEditingMenuViewModel(CreateSchoolProfileViewModel createSchoolProfileViewModel, FDataBaseTeachers storage, FDataBaseDisciplines storageDisciplines)    {        _storage = storage;        _storageDisciplines = storageDisciplines;        Teachers = new ObservableCollection<Data.Models.SchoolTeachers>(_storage.SchoolTeachers);        Disciplines = new ObservableCollection<Data.Models.SchoolDiscipline>(_storageDisciplines.SchoolDisciplines);        AddNewTeacher = ReactiveCommand.Create(() =>        {            var schoolTeacher = new Data.Models.SchoolTeachers("Новое ФИО учителя");            _storage.AddTeacher(schoolTeacher);            Teachers.Add(schoolTeacher);            return;        });        DeleteTeacher = ReactiveCommand.Create(() =>        {            _storage.DeleteDiscipline(_dataGridSelectedIndex);            Teachers.Remove(Teachers[_dataGridSelectedIndex]);        });    }    public bool IsEnabledContinue    {         set => this.RaiseAndSetIfChanged(ref _isEnabledContinue, value);         get => _isEnabledContinue;     }        public int DataGridSelectedIndex    {         set => this.RaiseAndSetIfChanged(ref _dataGridSelectedIndex, value);         get => _dataGridSelectedIndex;     }        public string? UrlPathSegment { get; }    public IScreen HostScreen { get; }    public RoutingState Router { get; }}