using System.Reactive;using Data.Repositories;using Domain.UseCases;using ReactiveUI;using ReactiveUI.Fody.Helpers;namespace SchoolTimetabler.ViewModels;public class UserInformationViewModel : ViewModelBase, IRoutableViewModel, IScreen{    public UserInformationViewModel()    {        var userInteractor = new UserInteractor(UserRepository.GetInstance());        ConfirmUserSettings = ReactiveCommand.Create(() => { userInteractor.UserSet(Post, FullName); });    }    [Reactive] public string Post { get; set; }    [Reactive] public string FullName { get; set; }    public ReactiveCommand<Unit, Unit> ConfirmUserSettings { get; }    public string? UrlPathSegment { get; }    public IScreen HostScreen { get; }    public RoutingState Router { get; }}