using System.Reactive;using Data.Repositories;using Domain.UseCases;using ReactiveUI;namespace SchoolTimetabler.ViewModels;public class UserInformationViewModel : ViewModelBase, IRoutableViewModel, IScreen{    private string _post;    private string _fullName;        public ReactiveCommand<Unit, Unit> ConfirmUserSettings { get; }        public UserInformationViewModel()    {        var userInteractor = new UserInteractor(UserRepository.GetInstance());                ConfirmUserSettings = ReactiveCommand.Create(() => { userInteractor.UserSet(_post, _fullName); });    }        public string FullName    {        set => this.RaiseAndSetIfChanged(ref _fullName, value);        get => _fullName;    }        public string Post    {        set => this.RaiseAndSetIfChanged(ref _post, value);        get => _post;    }    public string? UrlPathSegment { get; }    public IScreen HostScreen { get; }    public RoutingState Router { get; }}