using System.Threading.Tasks;using Avalonia.ReactiveUI;using ReactiveUI;using SchoolTimetabler.ViewModels;namespace SchoolTimetabler.Views;public partial class MainWindow : ReactiveWindow<MainWindowViewModel>{    public MainWindow()    {        InitializeComponent();        // DataContext = new MainWindowViewModel();        this.WhenActivated(disposables => { });        this.WhenActivated(d => d(ViewModel.ShowDialog.RegisterHandler(DoShowDialogAsync)));    }    private async Task DoShowDialogAsync(InteractionContext<SetPasswordViewModel, string?> interaction)    {        var dialog = new PasswordWindow();        dialog.DataContext = interaction.Input;                var result = await dialog.ShowDialog<string?>(this);        interaction.SetOutput(result);    }}